import React, { useState, useEffect, useMemo } from 'react';
import { ArrowRight, CheckCircle } from 'lucide-react';

export const QuizView = ({ 
  setView, 
  quizCode, 
  currentQuiz, 
  playerName, 
  userAnswers, 
  setUserAnswers,
  // Legacy props for compatibility
  quiz,
  userId,
  currentQuestionIndex: legacyCurrentQuestionIndex,
  selectedAnswer: legacySelectedAnswer,
  setSelectedAnswer,
  setCurrentQuiz,
  setQuizResults,
  setCurrentQuestionIndex,
  setLocalQuizzes,
  localQuizzes
}) => {
  const [currentQuestionIndex, setLocalCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [isLastQuestion, setIsLastQuestion] = useState(false);
  
  // Use currentQuiz if available, otherwise fallback to quiz prop for compatibility
  const activeQuiz = currentQuiz || quiz;
  const questions = useMemo(() => activeQuiz?.questions || [], [activeQuiz]);
  
  useEffect(() => {
    // Check if we're on the last question
    setIsLastQuestion(currentQuestionIndex === questions.length - 1);
    
    // Set selected option based on any previous answer for this question
    const questionId = questions[currentQuestionIndex]?.id || currentQuestionIndex;
    setSelectedOption(userAnswers[questionId] !== undefined ? userAnswers[questionId] : null);
  }, [currentQuestionIndex, questions, userAnswers]);
  
  const handleAnswerSelect = (optionIndex) => {
    // Allow changing answers - just update the selected option
    setSelectedOption(optionIndex);
    
    // Update userAnswers with the new selection
    const questionId = questions[currentQuestionIndex]?.id || currentQuestionIndex;
    setUserAnswers(prev => ({
      ...prev,
      [questionId]: optionIndex
    }));
    
    // For compatibility with legacy code
    if (setSelectedAnswer) {
      setSelectedAnswer(optionIndex);
    }
  };
  
  const handleNextQuestion = () => {
    if (selectedOption === null) {
      alert("Please select an answer before continuing.");
      return;
    }
    
    if (isLastQuestion) {
      handleFinishQuiz();
    } else {
      setLocalCurrentQuestionIndex(prev => prev + 1);
      
      // For compatibility with legacy code
      if (setCurrentQuestionIndex) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
      }
    }
  };
  
  const handleFinishQuiz = () => {
    console.log("Finishing quiz and showing results...");
    
    // Calculate real scores based on user answers
    let userScore = 0;
    questions.forEach((question, index) => {
      if (question.correctAnswer !== undefined && userAnswers[index] === question.correctAnswer) {
        userScore++;
      }
    });
    
    const realResults = {
      scores: [
        { name: playerName || "You", score: userScore },
        { name: "Demo Player 1", score: Math.floor(Math.random() * questions.length) },
        { name: "Demo Player 2", score: Math.floor(Math.random() * questions.length) }
      ],
      totalQuestions: questions.length
    };
    realResults.scores.sort((a, b) => b.score - a.score);
    
    if (setQuizResults) {
      setQuizResults(realResults);
    }
    setView('results');
  };
  
  // Early return for empty quiz
  if (!activeQuiz || questions.length === 0) {
    return (
      <div className="text-center">
        <p>No questions available for this quiz.</p>
        <button 
          onClick={() => setView('home')}
          className="bg-cyan-500 hover:bg-cyan-600 text-white font-bold py-2 px-4 rounded mt-4"
        >
          Return to Home
        </button>
      </div>
    );
  }
  
  const currentQuestion = questions[currentQuestionIndex];
  
  return (
    <div className="max-w-3xl mx-auto w-full">
      <div className="bg-gray-800 p-8 rounded-lg shadow-2xl">
        <div className="mb-6">
          <h2 className="text-2xl font-bold mb-2">{activeQuiz.title}</h2>
          <div className="bg-gray-700 h-2 rounded-full mb-2">
            <div 
              className="bg-cyan-500 h-2 rounded-full" 
              style={{ width: `${((currentQuestionIndex + 1) / questions.length) * 100}%` }}
            ></div>
          </div>
          <p className="text-sm">
            Question {currentQuestionIndex + 1} of {questions.length}
          </p>
        </div>
        
        <div className="mb-8">
          <h3 className="text-xl mb-4">{currentQuestion.text}</h3>
          
          <div className="grid grid-cols-1 gap-4">
            {currentQuestion.options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleAnswerSelect(index)}
                className={`w-full text-left p-4 rounded-lg text-lg transition-all duration-200 ${
                  selectedOption === index 
                    ? 'bg-yellow-500' 
                    : 'bg-gray-700 hover:bg-gray-600'
                }`}
              >
                {option}
                {selectedOption === index && (
                  <span className="float-right">
                    <CheckCircle className="inline ml-2" size={20} />
                  </span>
                )}
              </button>
            ))}
          </div>
        </div>
        
        <div className="mt-8 text-center">
          <button
            onClick={handleNextQuestion}
            className="bg-cyan-500 hover:bg-cyan-600 text-white font-bold py-3 px-8 rounded-lg text-lg"
          >
            {isLastQuestion ? 'Finish & Show Results' : 'Next Question'}
            {!isLastQuestion && <ArrowRight className="inline ml-2" size={20} />}
          </button>
        </div>
      </div>
    </div>
  );
};
